       --data_entrada,
       (select first 1 id_setor_trabalho
       from setor_trabalho
       where setor_trabalho.setor_trabalho like '%'||UPPER(i.setor_trabalho)||'%' ) idsetor_trabalho,
--       data_inicio_trabalho,
