  INTO :PAVILHAO_ATUAL;

  GALERIA_ATUAL = '(GALERIA ATUAL - NAO INFORMADO)' ;
  SELECT G.GALERIA
  FROM GALERIA G
  WHERE G.ID_GALERIA = NEW.IDGALERIA
  INTO :GALERIA_ATUAL;

