  MOVIMENTACAO = MOVIMENTACAO||'RESPONSAVEL DAS INFORMAES ANTERIORES:
'||COALESCE(NOME_FUNC_ANTES,'');
  MOVIMENTACAO = MOVIMENTACAO||'
----------------------------------------------------------------
' ;
  MOVIMENTACAO = MOVIMENTACAO||'RESPONSAVEL PELO REGISTRO ATUAL:
'||COALESCE(NOME_FUNC_ATUAL,'');

