ANTES........: '||pavilhao_antes||'/'||galeria_antes||'/'||solario_antes||'/'||cela_antes||'
ATUAL........: '||pavilhao_atual||'/'||galeria_atual||'/'||solario_atual||'/'||cela_atual;
  END

  IF (NEW.idcondicao_interno <> OLD.idcondicao_interno) THEN
  BEGIN
  MOVIMENTACAO = MOVIMENTACAO||'
----------------------------------------------------------------
