ALTER PROCEDURE SP_COPY (
    VALOR VARCHAR(32000),
    CARAC_INI INTEGER,
    QTDE INTEGER)
RETURNS (
    RESULTADO_COPY VARCHAR(32000))
AS
declare variable i integer;
declare variable j integer;
declare variable k integer;
BEGIN
  /*
  Copia de "CARAC_INI" "QTDE" bytes de "VALOR"
  Este recurso no  mais necessrio no FB 2
  Pois j aceita o substring com variveis
  */
  i = STRLEN(:VALOR);
  j = 1;
  k = 1;
  RESULTADO_COPY = '';
  
  WHILE ((:VALOR IS NOT NULL) AND (:i > 0) AND (j <= :QTDE)) DO
  BEGIN
    IF (:k >= :CARAC_INI) THEN
    BEGIN
    RESULTADO_COPY = :RESULTADO_COPY || SUBSTRING(:VALOR FROM 1 FOR 1);
    j = j + 1;
    END
  
    VALOR = SUBSTRING(:VALOR FROM 2 FOR 32000);
    k = :k + 1;
    i = :i - 1;
  END

SUSPEND;
END^


